# frozen_string_literal: true

# NOTE(RAJ): This is used by post and product embeds.
module EmbedSvg
  extend self

  THEMES = {
    'light' => {
      background: '#FFFFFF',
      border: '#FF6154',
      text: '#FF6154',
      icon: :default_logo,
    },
    'neutral' => {
      background: '#FFFFFF',
      border: '#D9E1EC',
      text: '#4B587C',
      icon: :neutral_logo,
    },
    'dark' => {
      background: '#221D21',
      border: '#221D21',
      text: '#EEF2FF',
      icon: :dark_logo,
    },
  }.freeze

  SIZES = ['small', 'large'].freeze

  def generate(icon:, title:, subtitle:, theme: nil, upvote_count: :skip, metric_type: nil, size: nil, small_size_title: nil)
    size = SIZES.include?(size) ? size : 'large'
    colors = THEMES[theme || 'light'] || THEMES['light']
    icon = colors[:icon] if icon == :default_logo && theme != 'light'
    small_size_title = title if small_size_title.nil?

    size == 'small' ? generate_small_svg(title: small_size_title, colors: colors) : generate_large_svg(icon: icon, title: title, subtitle: subtitle, colors: colors, upvote_count: upvote_count, metric_type: metric_type)
  end

  private

  def generate_large_svg(icon:, title:, subtitle:, colors:, upvote_count: :skip, metric_type: nil)
    metric = metric_type == :upvotes ? render_upvotes(upvote_count, colors) : render_review(colors)

    <<~SVG
      <?xml version="1.0" encoding="UTF-8"?>
      <svg width="250" height="54" viewBox="0 0 250 54" version="1.1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
        <g stroke="none" stroke-width="1" fill="none" fill-rule="evenodd">
          <g transform="translate(-130.000000, -73.000000)">
            <g transform="translate(130.000000, 73.000000)">
              <rect stroke="#{ colors[:border] }" stroke-width="1" fill="#{ colors[:background] }" x="0.5" y="0.5" width="249" height="53" rx="10"></rect>
              <text font-family="Helvetica-Bold, Helvetica" font-size="9" font-weight="bold" fill="#{ colors[:text] }">
                <tspan x="53" y="20">#{ title }</tspan>
              </text>
              <text font-family="Helvetica-Bold, Helvetica" font-size="#{ upvote_count == :skip ? 16 : 21 }" font-weight="bold" fill="#{ colors[:text] }">
                <tspan x="52" y="40">#{ subtitle }</tspan>
              </text>
              #{ metric_type.present? && metric }
              #{ ICONS[icon] }
            </g>
          </g>
        </g>
      </svg>
    SVG
  end

  def generate_small_svg(title:, colors:)
    <<~SVG
      <svg width="86" height="32" viewBox="0 0 86 32" fill="none" xmlns="http://www.w3.org/2000/svg">
        <rect x="0.5" y="0.5" width="85" height="31" rx="3.5" fill="#{ colors[:background] }"/>
        <path d="M16 24C20.4183 24 24 20.4183 24 16C24 11.5817 20.4183 8 16 8C11.5817 8 8 11.5817 8 16C8 20.4183 11.5817 24 16 24Z" fill="#{ colors[:text] }"/>
        <path fill-rule="evenodd" clip-rule="evenodd" d="M17.0666 16V16.0001H14.8V13.6001H17.0666V13.6C17.7294 13.6 18.2666 14.1373 18.2666 14.8C18.2666 15.4627 17.7294 16 17.0666 16ZM17.0666 12V12.0001L13.2 12V20H14.8V17.6001H17.0666V17.6C18.613 17.6 19.8666 16.3464 19.8666 14.8C19.8666 13.2536 18.613 12 17.0666 12Z" fill="#{ colors[:background] }"/>
        <text x="30" y="21" font-family="Helvetica-Bold, Helvetica" font-size="14" line-height="14" font-weight="bold" letter-spacing="0.5px" fill="#{ colors[:text] }">#{ title }</text>
        <rect x="0.5" y="0.5" width="85" height="31" rx="3.5" stroke="#{ colors[:border] }"/>
      </svg>
    SVG
  end

  def render_upvotes(upvote_count, colors)
    return if upvote_count == :skip

    # NOTE(rstankov): We have to center the number of upvotes
    upvote_x = 19 - upvote_count.to_s.size * 3.3

    <<-SVG
    <g transform="translate(201.000000, 13.000000)" fill="#{ colors[:text] }">
      <g>
        <polygon points="26.0024997 10 15 10 20.5012498 0"></polygon>
        <text font-family="Helvetica-Bold, Helvetica" font-size="13" font-weight="bold" line-spacing="20">
          <tspan x="#{ upvote_x }" y="27">#{ upvote_count }</tspan>
        </text>
      </g>
    </g>
    SVG
  end

  # Icon + Rating
  def render_review(colors)
    <<-SVG
      <g transform="translate(205.000000, 11.000000) scale(1.4)" fill="#{ colors[:text] }">
        #{ ICONS[:review] }
      </g>
    SVG
  end

